import re

str =\
r"""
1998
"""

print(str)
zhengze_list = ["[-+]", "[:]", "[/]", "[*]", "[%&]", "[$^]", "[#@!]", "[\\\\]", '[\\"]', "[\\']", "[_]",
"http[s]?://", "[</>]", "([a-zA-Z]|[0-9])", "([0-9])+", "([()])", "([（）])", "[(](.*?)[)]", "[(](.*)[)]",
"([(](.*?)[)])", "[(].*?[)]", "[(].*[)]", "[（].*?[）]", "[\[].*?[\]]", "^\d{5,}$", "^\d{5}$", "\d{5}", "\d{5,}",
r"^[\u4e00-\u9fa5]{0,}$", r"[\u4e00-\u9fa5]{0,}", "^.{3,20}$", ".{3,20}", "\w+", "\W+", "\s", "\S", "\d+","\D+",
"\A", "\Z", "\\b10\\b", "\\.*\\b", "\\b1\\b", "\\b\d+\\b", "\\B\d+\\B",
"\w+([-+.]\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*",
"[a-zA-Z0-9][-a-zA-Z0-9]{0,62}(/.[a-zA-Z0-9][-a-zA-Z0-9]{0,62})+/.?", "[a-zA-z]+://[^\s]*",
"^(13[0-9]|14[5|7]|15[0|1|2|3|5|6|7|8|9]|18[0|1|2|3|5|6|7|8|9])\d{8}$",
"\d{3}-\d{8}|\d{4}-\d{7}", "^\d{15}|\d{18}$", "^([0-9]){7,18}(x|X)?$", "^\d{8,18}|[0-9x]{8,18}|[0-9X]{8,18}?$",
"^[a-zA-Z]\w{5,17}$", "([a-z]+)([a-z]+)\\1", "([a-z]+)([a-z]+)\\2"]

for i in range(len(zhengze_list)):
    exec(r"""sentences%s = r're.findall(r"%s", str)'""" % (i, zhengze_list[i]))  # 变量赋值
    exec('print("sentences{}=".format(i),end = "")')  # sentences0 sentences1 sentences2 ... 变量名
    print(eval("sentences%s"%(i)), end="")  # re.findall(r"[-+]", str)    变量值
    print("  # ", eval(r're.findall(r"%s", str)'%(zhengze_list[i])))
